mapper.select:
SELECT COUNT(*)
FROM memberHost h
JOIN member1 m1 ON h.mhId = m1.memberId

	
		
	
	
		AND mhSign = #{mhSign}
	
	
		AND mhEnabled = #{mhEnabled}

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT  h.mhId, memberName, mhName, mhPhone, mhSign, h.mhEnabled, m1.memberAuth
		FROM memberHost h
		JOIN member1 m1 ON h.mhId = m1.memberId
		
			
				
			
			
				AND mhSign = #{mhSign}
			
			
				AND mhEnabled = #{mhEnabled}
			
		
		ORDER BY MEMBERREG_DATE DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
