mapper.select:
	SELECT * FROM (
	      	 SELECT ROWNUM rnum, tb.* FROM (
	            SELECT r.roomNum, r.roomName, lodgName, TO_CHAR(resCout_date, 'YYYY.MM.DD')resCout_date ,revNum, rd.resNum, l.mhId 
	            FROM member1 m
				JOIN reservation rv ON rv.memberId = m.memberId
				JOIN reservationDetail rd ON rv.resNum = rd.resNum
				JOIN room r ON rd.roomNum = r.roomNum
				JOIN lodging l ON l.mhId = r.mhId
			LEFT JOIN review review ON review.resNum = rv.resNum
			WHERE resCout_date < SYSDATE AND resCode=0 AND m.memberId= #{memberId}


					AND revNum IS NULL


					AND revNum IS NOT NULL


			ORDER BY rv.resCout_date DESC				 
        ) tb WHERE ROWNUM <= #{end}
    ) WHERE rnum >= #{start}

SELECT NVL(COUNT(*), 0) FROM member1 m JOIN reservation rv ON rv.memberId = m.memberId JOIN reservationDetail rd ON rv.resNum = rd.resNum JOIN room r ON rd.roomNum = r.roomNum JOIN lodging l ON l.mhId = r.mhId LEFT JOIN review review ON review.resNum = rv.resNum WHERE resCout_date < SYSDATE AND resCode=0 AND m.memberId= #{memberId}
AND revNum IS NULL
AND revNum IS NOT NULL
