mapper.select:
SELECT * FROM (
	SELECT DISTINCT((
		SELECT SUM(pointQuant) FROM point WHERE pointCode = 0 AND memberId = '#{memberId}') -
		(SELECT SUM(pointQuant) FROM point WHERE pointCode = 1 AND memberId = '#{memberId}')) total,
		pointQuant, p.memberId, pointCode, TO_CHAR(pointDate, 'YYYY-MM-DD') pointDate
		FROM point p
		JOIN member1 m ON p.memberId=m.memberId
		GROUP BY p.memberId, pointCOde, pointDate, pointQuant
) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
